function Comments(){
    return(
       <div className="comments">
            <h1 className="comments__title">JOIN THE CONVERSATION</h1>
            <img className="row__displaypic" src="../assets/Images/Mohan-muruge.jpg" alt="comments_displaypic"></img>
            <form className="comments__submitform">
                <input type="text" id="comment" name="comment" placeholder="Add a new comment"></input>
                <input type="submit" value="Comment"></input>
            </form>
            <div className="comments__preloaded">
                <p className="comments__preloaded--1">placeholder for comment 1</p>
                <p className="comments__preloaded--2">placeholder for comment 2</p>
                <p className="comments__preloaded--3">placeholder for comment 3</p>
            </div>
       </div>
    );
}
export default Comments;
