import logo from './logo.svg';
import './styles/App.css';
import Header from './components/Header';
import Row from './components/Row';
import Upload from './components/Upload';
import Video from './components/Video';
import Title from './components/Title';
import Description from './components/Description';
import Comments from './components/Comments';
import NextVideos from './components/NextVideos';
import videodata from './assets/Data/videos.json';
import videodatadet from './assets/Data/video-details.json';
import {useState} from 'react';

console.log(videodata);
console.log(videodatadet);

function App() {
  const v = videodatadet.map((vd)=>{
    vd.id.array.forEach(element => {
       console.log(element)
    });
  })
  console.log("this is array v")
  console.log(v)
  const [activeVideo,setActiveVideo]=useState(videodatadet[0]);
  const [nextVideos,setNextVideos]=useState(v);//array of information next video requires; id,img url, title, uploader (full-currently active)
  const handleClickVideo= (video)=>{
    //1.find full object based on video.id and set in setActiveVideo with this full object
    //2.reconstruct nextvideos array of objects
    //3. set nextvideos with a new value in step 2
    setActiveVideo(video)
  }


  return (
    <>
      <Header />
      <Row />
      <Upload />
      <Video />
      <Title />
      <Description />
      <Comments />
      <NextVideos handleClickVideo={handleClickVideo} videos={nextVideos}/>
    </>
  );
}

export default App;
