    console.log("this is inside comments:",typeof(props.activeVideo.comments))
    return(
       <div className="comments">

            <h2 className="comments__commentlength">{props.activeVideo.comments.length} Comments</h2>

            <h2 className="comments__title">JOIN THE CONVERSATION</h2>

            <img className="comments__displaypic" src="" alt="comments_displaypic"></img>

            <form className="comments__submitform">
                <input type="text" id="comment" name="comment" placeholder="Add a new comment"></input>
                <input type="submit" value="Comment"></input>
            </form>
        </div>            
    );

    const com = props.activeVideo.comments.map((c)=>{
        return(
            <div className="comments__loaded"> </div>

        )
    });

    return(
        {com}
    );


}
